import React from 'react';
import axios from 'axios';

export default async function Comments(props) {
        var URLString = 'http://localhost:5000/rate?noteid=' + props.noteid;
        let listOfComments = '';
        try {
            const response = getComments(URLString);
            console.log(response.data);
            var comments = response.data;
            console.log(Comment);
            if (comments && comments.length > 0) {
                listOfComments = comments.map((comment, idx) =>
                    <div className = "comments-box" key = { idx } >
                        <h3 className = "description" >{ comment.username }:- </h3> 
                        <p className = "author" >{ comment.comments } </p> 
                    </div>);
            }
            console.log('list of comments', listOfComments);
        }      
        catch(error) {
            console.log(error);
            alert("No Data found!");
        }
        return ( <div> <h2>Comments:-</h2>{ listOfComments } </div>);
}

async function getComments (URLString) {
    var response;
    try {
            const response = await axios.get(URLString);
            console.log(response.data);
        }      
        catch(error) {
            console.log(error);
            alert("No Data found!");
        }
    return (response);
}
