import React, { useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

const useFormInput = initialvalue => {
    const [value, setValue] = useState(initialvalue);
    const handleChange = e => {
        setValue(e.target.value);
    }
    return { value, onChange: handleChange };
}

export default function uploadNotes() {
    const author = useFormInput('');
    const subject = useFormInput('');
    const description = useFormInput('');
    const tags = useFormInput('');
    const handleUploadNotes = () => {
        axios.post("http://localhost:5000/notes", {
            "author": author.value,
            "subject": subject.value,
            "description": description.value,
            "tags": tags.value,
            "token": number.value
        }).then(response => {
            alert("Notes uploaded Successfully!");
        }).catch(error => {
            alert("Error");
        })
    }
    return ( 
        <div className = "lightbox" >
        <div className = "log-in-btn-box sign-in-btn-box" >
        <img className = "log-in-icon" src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSupwmCC0rO41tqGSaeTQNk6Kso9XgNNa89vg&usqp=CAU" alt = ""/>
        <div className = "note-head" > NotesHUB</div> 
        <Link to = "/" > <img className = "log-cross" src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8bx_5Auv6KmlC8_iD9xwhwTjxV0U624mlvA&usqp=CAU" alt = ""/>
        </Link> 
        <input type = "text" {...author } className = "log-in-input sign-in-input" placeholder = "Author"/>
        <input type = "text" {...subject } className = "log-in-input sign-in-email" placeholder = "Subject"/>
        <input type = "text" {...description } className = "log-in-input sign-in-phone" placeholder = "Description"/>
        <input type = "text" {...tags } className = "log-in-input sign-in-user" placeholder = "Tags"/>
        <input type = "file"/>
        <Link to="/"><button className = "log-in-box-btn sign-box-btn" onClick = { handleUploadNotes } > Upload </button></Link>
        </div> 
        </div >
    )
}
